package io.intino.ness.datahubterminalplugin;

import io.intino.itrules.RuleSet;
import io.intino.itrules.Template;

public class TerminalTemplate extends Template {

	public RuleSet ruleSet() {
		return new RuleSet().add(
				rule().condition((type("terminal"))).output(literal("package ")).output(mark("package", "validPackage")).output(literal(";\n\nimport ")).output(mark("package", "validPackage")).output(literal(".events.*;\nimport java.util.List;\n\npublic class ")).output(mark("name", "snakeCaseToCamelCase", "firstUpperCase")).output(literal(" {\n\n\tprivate final io.intino.alexandria.event.EventHub eventHub;\n\tprivate java.util.Map<java.util.function.Consumer<?>, java.util.function.Consumer<io.intino.alexandria.event.Event>> consumers = new java.util.HashMap<>();\n\n\tpublic static String[] subscriptionChannels = new String[]{")).output(mark("subscribe", "channel")).output(literal("};\n\n\tpublic ")).output(mark("name", "snakeCaseToCamelCase", "firstUpperCase")).output(literal("(io.intino.alexandria.event.EventHub eventHub) {\n\t\tthis.eventHub = eventHub;\n\t}\n\n\tpublic void publish(Object event, String context) {\n\t\t")).output(mark("publish", "if").multiple("\n")).output(literal("\n\t}\n\n\t")).output(mark("publish").multiple("\n\n")).output(literal("\n\n\t")).output(mark("subscribe").multiple("\n\n")).output(literal("\n\n\t")).output(mark("event", "interface").multiple("\n\n")).output(literal("\n\n\tpublic void stop() {\n\t\t//eventHub.stop();\n\t}\n}")),
				rule().condition((type("multicontext")), (trigger("if"))).output(literal("   if (event instanceof ")).output(mark("type")).output(literal(") publish((")).output(mark("type")).output(literal(") event, ")).output(mark("type", "FirstUpperCase")).output(literal(".Context.contextByQn(context));")),
				rule().condition((trigger("if"))).output(literal("   if (event instanceof ")).output(mark("type")).output(literal(") publish((")).output(mark("type")).output(literal(") event);")),
				rule().condition((type("multicontext")), (trigger("publish"))).output(literal("public void publish(")).output(mark("type")).output(literal(" ")).output(mark("typeName", "firstLowerCase")).output(literal(", ")).output(mark("type", "FirstUpperCase")).output(literal(".Context context, ")).output(mark("type", "FirstUpperCase")).output(literal(".Context... moreContexts) {\n\teventHub.sendEvent(context.qn() + \".")).output(mark("type")).output(literal("\", ")).output(mark("typeName", "firstLowerCase")).output(literal(");\n\tfor (")).output(mark("type", "FirstUpperCase")).output(literal(".Context c : moreContexts)\n\t\teventHub.sendEvent(c.qn() + \".")).output(mark("type")).output(literal("\", ")).output(mark("typeName", "firstLowerCase")).output(literal(");\n}")),
				rule().condition((trigger("publish"))).output(literal("public void publish(")).output(mark("type")).output(literal(" ")).output(mark("typeName", "firstLowerCase")).output(literal(") {\n\teventHub.sendEvent(\"")).output(mark("channel")).output(literal("\", ")).output(mark("typeName", "firstLowerCase")).output(literal(");\n}")),
				rule().condition((type("multiContext")), (trigger("subscribe"))).output(literal("public void subscribe(")).output(mark("typeName", "FirstUpperCase")).output(literal("Consumer onEventReceived, String subscriberId, ")).output(mark("type", "FirstUpperCase")).output(literal(".Context context, ")).output(mark("type", "FirstUpperCase")).output(literal(".Context... moreContexts) {\n\tconsumers.put(onEventReceived, event -> onEventReceived.accept(new ")).output(mark("type")).output(literal("(event)));\n\teventHub.attachListener(context.qn() + \".")).output(mark("type")).output(literal("\", subscriberId, consumers.get(onEventReceived));\n\tfor (")).output(mark("type", "FirstUpperCase")).output(literal(".Context c : moreContexts)\n\t\teventHub.attachListener(c.qn() + \".")).output(mark("type")).output(literal("\", subscriberId, consumers.get(onEventReceived));\n}\n\npublic void subscribe(")).output(mark("typeName", "FirstUpperCase")).output(literal("Consumer onEventReceived, ")).output(mark("type", "FirstUpperCase")).output(literal(".Context context, ")).output(mark("type", "FirstUpperCase")).output(literal(".Context... moreContexts) {\n\tconsumers.put(onEventReceived, event -> onEventReceived.accept(new ")).output(mark("type")).output(literal("(event)));\n\teventHub.attachListener(context.qn() + \".")).output(mark("type")).output(literal("\", subscriberId, consumers.get(onEventReceived));\n\tfor (")).output(mark("type", "FirstUpperCase")).output(literal(".Context c : moreContexts)\n\t\teventHub.attachListener(c.qn() + \".")).output(mark("type")).output(literal("\", consumers.get(onEventReceived));\n}\n\npublic void unsubscribe(")).output(mark("type", "FirstUpperCase")).output(literal("Consumer onEventReceived) {\n\teventHub.detachListeners(consumers.get(onEventReceived));\n}")),
				rule().condition((trigger("subscribe"))).output(literal("public void subscribe(")).output(mark("typeName", "FirstUpperCase")).output(literal("Consumer onEventReceived, String subscriberId) {\n\tconsumers.put(onEventReceived, event -> onEventReceived.accept(new ")).output(mark("type")).output(literal("(event)));\n\teventHub.attachListener(\"")).output(mark("channel")).output(literal("\", subscriberId, consumers.get(onEventReceived));\n}\n\npublic void subscribe(")).output(mark("typeName", "FirstUpperCase")).output(literal("Consumer onEventReceived) {\n\tconsumers.put(onEventReceived, event -> onEventReceived.accept(new ")).output(mark("type")).output(literal("(event)));\n\teventHub.attachListener(\"")).output(mark("channel")).output(literal("\", consumers.get(onEventReceived));\n}\n\npublic void unsubscribe(")).output(mark("typeName", "FirstUpperCase")).output(literal("Consumer onEventReceived) {\n\teventHub.detachListeners(consumers.get(onEventReceived));\n}")),
				rule().condition((trigger("quoted"))).output(literal("\"")).output(mark("")).output(literal("\"")),
				rule().condition((trigger("interface"))).output(literal("public interface ")).output(mark("name")).output(literal("Consumer extends java.util.function.Consumer<")).output(mark("type")).output(literal("> {\n}"))
		);
	}
}